import React from 'react';
import moment from 'moment';
import Axios from 'axios';


export default function DisplayWeather(props) {
    const { location, country, currdt, temp, maxTemp, minTemp, main, description, icon, humidity, windSpeed, clouds, sunrise, sunset, } = props.weatherData;

    // function timeConverter(t) {
    //     var dt = new Date(t * 1000);
    //     var hr = dt.getHours();
    //     var m = "0" + dt.getMinutes();
    //     var s = "0" + dt.getSeconds();
    //     return hr + ':' + m.substr(-2) + ':' + s.substr(-2);
    // }
    var sunriseconv = moment.unix(sunrise).format("DD:MM:yyyy hh:MM:ss");
    var sunsetconv = moment.unix(sunset).format("hh:MM:ss");
    var currentdate =  moment.unix(currdt).format("MMMM Do, dddd");
    Axios.get(`http://openweathermap.org/img/wn/{icon}@2x.png`)

    return (
        <div className='main-body'>
            <div className='location'>
                {location}, {country}
            </div>
            <div className='current-date'>
                {currentdate}
            </div>
            <div className='main'>
                <div className='currentTemp'>
                    {temp}
                </div>
                <div className='icon'>
                    {icon}
                </div>
            </div>

            {maxTemp} <br />
            {minTemp} <br />
            {main} <br />
            {description} <br />
            {icon} <br />
            {humidity}%<br />
            {windSpeed} m/s <br />
            {clouds}% <br />
            {sunriseconv} <br />
            {sunsetconv} <br />
        </div>
    )
}
