import React from 'react';
import Axios from 'axios';

import './App.css';

class App extends React.Component {

  state = {
    coords: {
      latitude: 0,
      longitude: 0
    }
  }

  componentDidMount() {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        let newCoords = {
          latitude: position.coords.latitude,
          longitude: position.coords.longitude,
        }
        this.setState({ coords: newCoords })
        // console.log(this.state.coords)
        //Api Call
        Axios.get(`http://api.openweathermap.org/data/2.5/weather?lat=${this.state.coords.latitude}&lon=${this.state.coords.longitude}&appid=d3856252805c106a89495a655d00f5da`).then(res => {
          let weatherData = {
            loaction: res.name,
            // country: res.sys.country,
            // temp: res.main.temp,
            // maxTemp: res.main.temp_max,
            // minTemp: res.main.temp_min,
          }
        })
      })
    }
    else {
      console.log('Not supported')
    }
  }

  render() {
    return (
      <div className="App">

      </div>
    );
  }
}

export default App;
